export const initialState = {
    todos: [
        {
            todo: 'Learn about reducers',
            completed: false,
            id: Date.now
          },
          {
            todo: 'Learn about redux',
            completed: false,
            id: Date.now
          },
          {
            todo: 'Learn python',
            completed: false,
            id: Date.now
          }
    ]
}
  export const todoReducer = (state =[], action) => {
      switch (action.type) {
         case "ADD_TODO":
             return { ...state, 

                todos: [
                    ...state.todos,
                    {
                        key: Date.now,
                        todo: action.text,
                        completed: false
                      }
                ]
             };

        case "TOGGLE_COMPLETE":
            return { ...state, 

                todos: state.todos.map( todo =>
                    todo.id === action.id ? {
                        ...todo,
                        completed: !todo.completed
                    } : todo.id)
            };

            case 'CLEAR_COMPLETED' : 
                return {
                    ...state,
                    todos: state.todos.filter(todo => !todo.completed)
                };

        default:
            return state;
      }
  };
